function CheckSelection() {
    var selectedValue = $('#NumberSelector').val();
    var decimalPlaces = $('#DecimalPlaceSelector').val();

    if (selectedValue == '' || decimalPlaces == '') {
        return;
    }

    $('#Results').slideDown('slow');

    $('#DataTable').dataTable().fnClearTable();
    $('#Graph').html('');
    $('#Debug').text('');

    var nDecimalPlaces = Number(decimalPlaces);

    if (selectedValue == 'Square root of 2') {
        Big.DP = nDecimalPlaces;

        //var calculationString = CalculateSquareRootTwo(nDecimalPlaces * 2).toPrecision(nDecimalPlaces + 1);
        var calculationString = sqrt2().substring(0, nDecimalPlaces + 1);
        $('#CalculationResult').text(calculationString);
        ParseDecimals(calculationString);
    } else if (selectedValue == 'Pi') {
        var calculationString = pi().substring(0, nDecimalPlaces + 1);
        $('#CalculationResult').text(calculationString);
        ParseDecimals(calculationString);
    } else if (selectedValue == 'e') {
        var calculationString = e().substring(0, nDecimalPlaces + 1);
        $('#CalculationResult').text(calculationString);
        ParseDecimals(calculationString);
    } else {
        $('#CalculationResult').text(selectedValue + ' is not yet implemented');
    }
}

function CalculateSquareRootTwo(iterations) {
    if (iterations == 0) {
        return new Big(1);
    }

    var an = CalculateSquareRootTwo(iterations - 1);

    var anp1 = an.div(2).plus((new Big(1)).div(an))

    return anp1;
}

function sqrt2() {
    return '1.';
}

function pi() {
    return '3.';
}

function e() {
    return '2.';
}

function ParseDecimals(input) {
    var decimalFrequency = new Array();

    for (var i = 0; i < 10; i++) {
        decimalFrequency[i] = 0;
    }

    var total = 0;

    for (var i = 0; i < input.length; i++) {
        var n = Number(input.charAt(i));
        decimalFrequency[n] = decimalFrequency[n] + 1;
        total = total + 1;
    }

    var graphData = [];

    for (var i = 0; i < decimalFrequency.length; i++) {
        $('#DataTable').dataTable().fnAddData([i, decimalFrequency[i], decimalFrequency[i] / total]);
        graphData.push([i, decimalFrequency[i] / total]);
    }

    $.plot("#Graph", [{
        data: graphData,
        bars: { show: true, fill: true }
    }]);
}
